<!doctype html>
<html lang="ru">
  <head>
    <meta charset="UTF-8">
    <title>Document</title>
    <style>
      body{
        margin:0;
      }
      .red-box{
        position:relative;
        left:500px;
        width:200px;
        height:200px;
        background:rgba(255,0,0,.5);
        border:2px solid black;
      }
      .green-box{
        position:absolute;
        width:100px;
        height:100px;
        background:green;
        border:2px solid black;
      }
      .blue-box{
        position:absolute;
        width:70px;
        height:70px;
        background:blue;
        border:1px solid black;
      }
      .yellow-box{
        width:100px;
        height:100px;
        background:yellow;
      }
    </style>
  </head>
  <body>
    <!--
     red-box - 1
     green-box - 2
     blue-box -3
    -->
    <div class="yellow-box"></div>
    <div class="red-box">
      <div class="blue-box">3</div>
    </div>
    <div class="green-box">2</div>
